"use client";

import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { heroesData, type Character } from "@/data/filmData";
import CharacterModal from "./CharacterModal";
import { cn } from "@/lib/utils";
import SectionHeader from "./SectionHeader";

const categories = [
    { id: "tous", label: "Tous" },
    { id: "hros", label: "Héros" },
    { id: "comploteurs", label: "Comploteurs" },
    { id: "animaux", label: "Animaux" },
    { id: "creatures", label: "Créatures" },
    { id: "mchant", label: "XoroX" },
];

export default function CharacterGallery() {
    const [activeCategory, setActiveCategory] = useState<string>("tous");
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);

    const filteredCharacters = activeCategory === "tous"
        ? heroesData
        : heroesData.filter((c) => c.cat === activeCategory);

    return (
        <div className="w-full px-0 py-8 relative">
            <SectionHeader
                title="Les Factions"
                label="// CLASSIFICATION : ACTEURS"
                description="Profils des héros de la résistance, leurs antagonistes, et les créatures de la planète-prison."
                alignment="center"
            />
            {/* HUD-style horizontal filters */}
            <div className="flex items-center justify-start sm:justify-center gap-1 mb-10 px-4 sm:px-0 overflow-x-auto pb-4 custom-scrollbar whitespace-nowrap w-full">
                {categories.map((cat, i) => {
                    const isActive = activeCategory === cat.id;
                    return (
                        <React.Fragment key={cat.id}>
                            <motion.button
                                key={cat.id}
                                onClick={() => setActiveCategory(cat.id)}
                                className="group flex items-center gap-2 px-3 py-2 cursor-pointer"
                                aria-label={cat.label}
                                whileHover={{ scale: 1.1 }}
                                whileTap={{ scale: 0.92 }}
                                transition={{ type: "spring", stiffness: 400, damping: 20 }}
                            >
                                {/* Dot indicator */}
                                <div className="relative w-2 h-2 flex items-center justify-center">
                                    <motion.div
                                        className="rounded-full"
                                        animate={isActive
                                            ? { width: 6, height: 6, backgroundColor: "#25d1f4", boxShadow: "0 0 8px #25d1f4" }
                                            : { width: 4, height: 4, backgroundColor: "rgba(255,255,255,0.2)", boxShadow: "none" }
                                        }
                                        whileHover={{ scale: 1.5, backgroundColor: "#25d1f4" }}
                                        transition={{ type: "spring", stiffness: 300, damping: 20 }}
                                    />
                                    {isActive && <div className="absolute inset-0 rounded-full border border-teal-accent/40 animate-ping" />}
                                </div>
                                <motion.span
                                    className={`text-[9px] font-mono uppercase tracking-[0.25em] transition-colors duration-200 ${isActive ? "text-teal-accent" : "text-white/30 group-hover:text-white/80"
                                        }`}
                                    whileHover={{ letterSpacing: "0.35em" }}
                                    transition={{ duration: 0.2 }}
                                >
                                    {cat.label}
                                </motion.span>
                            </motion.button>
                            {i < categories.length - 1 && (
                                <div className="w-px h-3 bg-white/10" />
                            )}
                        </React.Fragment>
                    );
                })}
            </div>

            {/* Grid */}
            <motion.div
                layout
                className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-3"
            >
                <AnimatePresence mode="popLayout">
                    {filteredCharacters.map((char, index) => (
                        <motion.div
                            layout
                            key={char.name}
                            initial={{ opacity: 0, scale: 0.8, filter: "blur(10px)" }}
                            animate={{ opacity: 1, scale: 1, filter: "blur(0px)" }}
                            exit={{ opacity: 0, scale: 0.8, filter: "blur(10px)" }}
                            transition={{ duration: 0.4, delay: index * 0.05 }}
                            onClick={() => setSelectedCharacter(char)}
                            onMouseEnter={(e) => {
                                // Find the video element inside this card to play its audio quietly, or just rely on CSS hover 
                                // Since we don't have a dedicated SFX file, a neat trick is to quickly unmute the video if it has one, 
                                // or use a browser-generated beep. But the safest and most cinematic without a dedicated asset is to trigger the video's own sound briefly just at a barely audible level.
                                const video = e.currentTarget.querySelector('video');
                                if (video) {
                                    video.muted = false;
                                    video.volume = 0.2;
                                }
                            }}
                            onMouseLeave={(e) => {
                                const video = e.currentTarget.querySelector('video');
                                if (video) {
                                    video.muted = true;
                                }
                            }}
                            className="group cursor-pointer relative rounded-xl overflow-hidden aspect-[3/4] border border-glass-border bg-black hover:border-teal-accent/50 transition-colors"
                        >
                            {char.video || (char.img && char.img.endsWith('.mp4')) ? (
                                <video
                                    src={char.video || char.img}
                                    className="w-full h-full object-cover opacity-60 group-hover:opacity-100 transition-opacity duration-500 group-hover:scale-105"
                                    autoPlay
                                    muted
                                    loop
                                    playsInline
                                />
                            ) : (
                                <img
                                    src={char.img}
                                    alt={char.name}
                                    className="w-full h-full object-cover opacity-60 group-hover:opacity-100 transition-all duration-500 group-hover:scale-105 filter grayscale group-hover:grayscale-0"
                                    onError={(e) => {
                                        (e.target as HTMLImageElement).src = 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII='; // transparent 1x1 fallback
                                    }}
                                />
                            )}

                            <div className="absolute inset-0 bg-gradient-to-t from-black via-black/40 to-transparent opacity-80 group-hover:opacity-100 transition-opacity duration-500" />

                            <div className="absolute bottom-0 left-0 right-0 p-3 transform translate-y-1 group-hover:translate-y-0 transition-transform duration-300">
                                <h3 className="text-sm font-medium uppercase text-white mb-0.5 tracking-tight leading-tight">
                                    {char.name.split(' ')[0]}
                                </h3>
                                <p className="text-teal-accent text-[9px] font-normal tracking-[0.2em] uppercase opacity-80">
                                    {char.role}
                                </p>
                                <div className="h-0 group-hover:h-auto overflow-hidden opacity-0 group-hover:opacity-100 transition-all duration-400 mt-2 border-t border-white/15 pt-2">
                                    <p className="text-white/50 text-[10px] line-clamp-2 leading-relaxed font-light">
                                        {char.inspiration}
                                    </p>
                                </div>
                            </div>

                            {/* Sci-fi UI Elements */}
                            <div className="absolute top-4 right-4 w-2 h-2 bg-teal-accent rounded-full opacity-0 group-hover:opacity-100 transition-opacity duration-300 shadow-[0_0_10px_#25d1f4]" />
                            <div className="absolute top-4 left-4 text-[9px] font-mono text-white/30 tracking-widest uppercase">ID: {char.name.substring(0, 3)}</div>
                        </motion.div>
                    ))}
                </AnimatePresence>
            </motion.div>

            <CharacterModal
                character={selectedCharacter}
                onClose={() => setSelectedCharacter(null)}
            />
        </div>
    );
}

