"use client";

import { useState, useMemo, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { X, Download, PlayCircle, Shield, Cpu, Activity, Database, ChevronRight, Terminal } from "lucide-react";
import type { Character } from "@/data/filmData";

export default function CharacterModal({
    character,
    onClose,
}: {
    character: Character | null;
    onClose: () => void;
}) {
    const [activeIndex, setActiveIndex] = useState(0);
    const videoRef = useRef<HTMLVideoElement>(null);

    useEffect(() => {
        setActiveIndex(0);
    }, [character]);

    const mediaList = useMemo(() => {
        if (!character) return [];
        const items = [];
        if (character.classe) items.push({ type: 'image', url: character.classe, label: "VUE 3D / CLASSE" });
        if (character.video) items.push({ type: 'video', url: character.video, label: "VIDÉO PROFIL" });
        if (character.img && character.img !== character.classe) items.push({ type: 'image', url: character.img, label: "ILLUSTRATION" });
        if (character.portrait) items.push({ type: 'image', url: character.portrait, label: "PORTRAIT" });
        if (character.model) items.push({ type: 'image', url: character.model, label: "MODEL SHEET" });
        return items;
    }, [character]);

    useEffect(() => {
        if (!character) return;
        const activeMedia = mediaList[activeIndex];
        const isVideoPlaying = activeMedia?.type === 'video';

        if (isVideoPlaying) {
            window.dispatchEvent(new CustomEvent('character-video-start'));
        } else {
            window.dispatchEvent(new CustomEvent('character-video-stop'));
        }

        return () => {
            window.dispatchEvent(new CustomEvent('character-video-stop'));
        };
    }, [activeIndex, mediaList, character]);

    useEffect(() => {
        return () => {
            window.dispatchEvent(new CustomEvent('character-modal-close'));
        };
    }, []);

    if (!character) return null;

    const activeMedia = mediaList[activeIndex];

    return (
        <AnimatePresence>
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                onClick={onClose}
                className="fixed inset-0 z-[100] bg-black/98 backdrop-blur-2xl flex items-center justify-center p-4 md:p-8 cursor-pointer"
            >
                {/* Global HUD Layer */}
                <div className="absolute inset-0 pointer-events-none opacity-[0.05] z-[101] bg-[linear-gradient(rgba(18,16,16,0)_50%,rgba(0,0,0,0.25)_50%),linear-gradient(90deg,rgba(255,0,0,0.06),rgba(0,255,0,0.02),rgba(0,0,255,0.06))] bg-[length:100%_2px,3px_100%]" />

                <motion.div
                    initial={{ y: 30, opacity: 0, scale: 0.98 }}
                    animate={{ y: 0, opacity: 1, scale: 1 }}
                    exit={{ y: 20, opacity: 0, scale: 0.98 }}
                    transition={{ duration: 0.6, ease: [0.16, 1, 0.3, 1] }}
                    onClick={(e) => e.stopPropagation()}
                    className="w-full max-w-[1400px] max-h-[75vh] bg-[#010203] border border-teal-accent/15 rounded-sm shadow-[0_0_120px_rgba(0,0,0,1)] overflow-hidden flex flex-col md:flex-row relative cursor-default"
                >
                    {/* Visual Interface Column - 60% Width for Cinematic focus */}
                    <div className="w-full md:w-[60%] relative flex flex-col bg-[#050608] border-r border-teal-accent/5">
                        {/* Upper Interface Bar */}
                        <div className="h-10 border-b border-teal-accent/10 flex items-center justify-between px-6 bg-black/40">
                            <div className="flex items-center gap-4">
                                <Activity size={12} className="text-teal-accent/50 animate-pulse" />
                                <span className="text-[8px] font-mono text-teal-accent/40 uppercase tracking-[0.4em]">VISUAL_DUMP // LIVE_FEED</span>
                            </div>
                            <div className="flex gap-1">
                                <span className="text-[7px] font-mono text-white/20 mr-4">LATENCY: 12ms</span>
                                <div className="w-1 h-1 bg-teal-accent/20" />
                                <div className="w-1 h-1 bg-teal-accent/20" />
                                <div className="w-1 h-1 bg-teal-accent/40" />
                            </div>
                        </div>

                        {/* Main Media Viewer */}
                        <div className="flex-1 relative flex items-center justify-center p-4 bg-gradient-to-b from-black to-[#080a0c] overflow-hidden">
                            <AnimatePresence mode="wait">
                                <motion.div
                                    key={activeMedia?.url}
                                    initial={{ opacity: 0, scale: 1.02, filter: "brightness(2) contrast(1.2) blur(10px)" }}
                                    animate={{ opacity: 1, scale: 1, filter: "brightness(1) contrast(1) blur(0px)" }}
                                    exit={{ opacity: 0, scale: 0.98 }}
                                    transition={{ duration: 0.5 }}
                                    className="w-full h-full relative z-10"
                                >
                                    {activeMedia?.type === 'video' ? (
                                        <video
                                            ref={videoRef}
                                            src={activeMedia.url}
                                            autoPlay loop playsInline
                                            className="w-full h-full object-contain"
                                        />
                                    ) : (
                                        <img
                                            src={activeMedia?.url}
                                            className="w-full h-full object-contain"
                                            alt={character.name}
                                        />
                                    )}
                                </motion.div>
                            </AnimatePresence>

                            {/* Viewport UI Overlays - Scaled for landscape */}
                            <div className="absolute inset-0 pointer-events-none z-20">
                                <div className="absolute top-4 left-4 w-8 h-8 border-t border-l border-teal-accent/30" />
                                <div className="absolute top-4 right-4 w-8 h-8 border-t border-r border-teal-accent/30" />
                                <div className="absolute bottom-4 left-4 w-8 h-8 border-b border-l border-teal-accent/30" />
                                <div className="absolute bottom-4 right-4 w-8 h-8 border-b border-r border-teal-accent/30" />

                                <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-48 h-px bg-teal-accent/5" />
                                <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-px h-48 bg-teal-accent/5" />

                                <div className="absolute bottom-6 left-6 flex flex-col gap-1">
                                    <span className="text-[9px] font-mono text-teal-accent/80 uppercase tracking-[0.2em] bg-teal-accent/5 px-3 py-1 border-l border-teal-accent shadow-[0_0_15px_rgba(37,209,244,0.1)]">
                                        SOURCE_{activeIndex}: {activeMedia?.label || "NONE"}
                                    </span>
                                </div>
                            </div>
                        </div>

                        {/* Media Selector Strip - Compact for landscape */}
                        <div className="h-20 bg-black/60 border-t border-teal-accent/10 px-8 flex items-center gap-4 overflow-x-auto disabled-scrollbar">
                            {mediaList.map((media, idx) => (
                                <button
                                    key={idx}
                                    onClick={() => setActiveIndex(idx)}
                                    className={`relative shrink-0 w-24 h-12 rounded-sm overflow-hidden border transition-all duration-500 group/thumb ${activeIndex === idx
                                            ? 'border-teal-accent/60 scale-105 shadow-[0_0_20px_rgba(37,209,244,0.25)]'
                                            : 'border-white/5 opacity-40 hover:opacity-100 hover:border-white/20'
                                        }`}
                                >
                                    {media.type === 'video' ? (
                                        <div className="w-full h-full relative">
                                            <video src={media.url} className="w-full h-full object-cover muted" />
                                            <div className="absolute inset-0 flex items-center justify-center bg-black/40 group-hover/thumb:bg-teal-accent/20 transition-colors">
                                                <PlayCircle size={16} className="text-white/60" />
                                            </div>
                                        </div>
                                    ) : (
                                        <img src={media.url} className="w-full h-full object-cover" alt="" />
                                    )}
                                    {activeIndex === idx && (
                                        <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-teal-accent" />
                                    )}
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Intelligence Report Column - 40% Width */}
                    <div className="w-full md:w-[40%] p-8 md:p-12 lg:p-14 flex flex-col gap-8 overflow-y-auto custom-scrollbar relative bg-[#010203]">
                        {/* Close button */}
                        <button
                            onClick={onClose}
                            className="absolute top-6 right-6 text-white/20 hover:text-teal-accent transition-colors z-50 p-2"
                        >
                            <X size={24} strokeWidth={1.5} />
                        </button>

                        {/* Header Section */}
                        <div className="space-y-4 relative">
                            <div className="absolute -top-4 -left-2 text-[80px] font-black font-mono text-white/[0.015] pointer-events-none select-none leading-none tracking-tighter">
                                {character.cat.slice(0, 4).toUpperCase()}
                            </div>

                            <div className="flex items-center gap-3">
                                <div className="h-px w-6 bg-teal-accent/40" />
                                <span className="text-[9px] font-mono text-teal-accent/60 uppercase tracking-[0.5em] leading-none">
                                    CLASSIFIED_DOSSIER
                                </span>
                            </div>

                            <div className="space-y-1">
                                <h2 className="text-5xl md:text-6xl font-bold uppercase tracking-tighter text-white leading-[0.9]">
                                    {character.name}
                                </h2>
                                <div className="flex items-center gap-3">
                                    <div className="px-2 py-0.5 bg-teal-accent/10 border border-teal-accent/20 rounded-sm">
                                        <span className="text-[9px] font-mono text-teal-accent uppercase tracking-widest">{character.role}</span>
                                    </div>
                                    <div className="h-px flex-1 bg-gradient-to-r from-teal-accent/20 to-transparent" />
                                </div>
                            </div>
                        </div>

                        {/* Info Blocks - More compact for landscape */}
                        <div className="flex flex-col gap-8">
                            {/* Bio Data */}
                            <div className="space-y-4">
                                <div className="flex items-center gap-2 opacity-30">
                                    <Database size={12} className="text-teal-accent" />
                                    <h4 className="text-[9px] font-bold tracking-[0.3em] uppercase text-white">DONNÉES_RÉPERTORIÉES</h4>
                                </div>

                                <div className="grid grid-cols-1 gap-3">
                                    <div className="p-3 bg-teal-accent/[0.02] border border-teal-accent/5 rounded-sm relative overflow-hidden">
                                        <div className="absolute top-0 left-0 w-0.5 h-full bg-teal-accent/30" />
                                        <div className="flex flex-col gap-1">
                                            <span className="text-[7px] font-mono text-white/30 uppercase tracking-[0.2em]">OCCUPATION_ID :</span>
                                            <span className="text-xs text-white/80 font-light">{character.realLife?.occ || "NON_IDENTIFIÉ"}</span>
                                        </div>
                                    </div>

                                    <div className="p-3 bg-teal-accent/[0.02] border border-teal-accent/5 rounded-sm relative overflow-hidden">
                                        <div className="absolute top-0 left-0 w-0.5 h-full bg-teal-accent/30" />
                                        <div className="flex flex-col gap-1">
                                            <span className="text-[7px] font-mono text-white/30 uppercase tracking-[0.2em]">ANALYSE_FAITS :</span>
                                            <span className="text-xs text-white/80 font-light">{character.realLife?.fact || "EN_COURS"}</span>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Behavioral Block */}
                            <div className="space-y-4">
                                <div className="flex items-center gap-2 opacity-30">
                                    <Terminal size={12} className="text-teal-accent" />
                                    <h4 className="text-[9px] font-bold tracking-[0.3em] uppercase text-white">SYNTHÈSE_COMPORTEMENTALE</h4>
                                </div>

                                <div className="relative p-6 bg-white/[0.015] border border-white/5 rounded-sm group/essay">
                                    <div className="absolute top-0 left-0 w-[2px] h-6 bg-teal-accent/40" />
                                    <blockquote className="text-base text-white/70 leading-relaxed italic font-light">
                                        "{character.inspiration}"
                                    </blockquote>
                                </div>
                            </div>
                        </div>

                        {/* Compact Footer */}
                        <div className="mt-auto pt-6 border-t border-white/5 flex items-center justify-between opacity-20">
                            <span className="text-[7px] font-mono tracking-widest uppercase">LEVEL: OMEGA-4 // TS: {new Date().toISOString().substring(11, 19)}</span>
                            <span className="text-[7px] font-mono text-teal-accent tracking-[0.6em] uppercase">STABLE</span>
                        </div>
                    </div>
                </motion.div>
            </motion.div>
        </AnimatePresence>
    );
}

"use client";

import { motion, AnimatePresence } from "framer-motion";
import { Terminal, Lightbulb, Copy, Check, X, Expand } from "lucide-react";
import { useState } from "react";
import dynamic from 'next/dynamic';
import { storyData } from "@/data/filmData";
import SectionHeader from "./SectionHeader";

const Artifact3D = dynamic(() => import("./Artifact3D"), { ssr: false });

export default function CreationSection() {
    const [copiedIndex, setCopiedIndex] = useState<number | null>(null);
    const [activeModal, setActiveModal] = useState<"images" | "videos" | null>(null);

    const handleCopy = (text: string, index: number) => {
        navigator.clipboard.writeText(text);
        setCopiedIndex(index);
        setTimeout(() => setCopiedIndex(null), 2000);
    };

    const renderImagePrompts = () => (
        <div className="space-y-6">
            {storyData.promptLibrary.images.map((prompt: string, i: number) => {
                const [title, ...descArr] = prompt.split(':');
                const desc = descArr.join(':').trim();

                const previewImages = [
                    "/assets/Scenes_action/grok-image-6f902406-d092-4aa2-bbc6-e2be03d47989.png",
                    "/assets/persos_de_reference/version_classe_hero/nicolas.png",
                    "/assets/persos_de_reference/XoroX/XOROX_GOOD.jpg",
                    "/assets/Scenes_action/andre_charles_chateau.png",
                    "/assets/Scenes_action/eclairage.png"
                ];
                const previewImage = previewImages[i] || previewImages[0];

                return (
                    <div key={i} className="group/prompt relative bg-teal-accent/[0.02] p-5 rounded-sm border border-teal-accent/5 hover:border-teal-accent/40 transition-all duration-300 flex flex-col md:flex-row gap-6 items-start md:items-center overflow-hidden">
                        <div className="absolute top-0 left-0 w-0.5 h-full bg-teal-accent/30 group-hover/prompt:bg-teal-accent transition-colors" />
                        <div className="flex-1 w-full font-mono text-sm z-10">
                            <div className="flex justify-between items-start mb-3">
                                <div className="flex items-center gap-3">
                                    <div className="w-1.5 h-1.5 bg-teal-accent/80 shadow-[0_0_8px_#25d1f4]" />
                                    <span className="text-teal-accent font-bold uppercase tracking-[0.2em] text-xs">{title}</span>
                                </div>
                                <button
                                    onClick={() => handleCopy(desc, i)}
                                    className="opacity-0 group-hover/prompt:opacity-100 transition-opacity p-2 hover:bg-teal-accent/10 rounded-sm text-teal-accent/50 hover:text-teal-accent"
                                    title="Copier le prompt"
                                >
                                    {copiedIndex === i ? <Check size={14} /> : <Copy size={14} />}
                                </button>
                            </div>
                            <div className="ml-4 pl-4 border-l border-teal-accent/10 text-white/70 leading-relaxed font-light">
                                <span className="text-teal-accent/30 mr-2 select-none tracking-widest text-[10px] uppercase">/imagine prompt:</span>
                                {desc}
                                <span className="text-teal-accent/20 select-none ml-2 tracking-widest text-[10px]">--ar 16:9 --v 6.0</span>
                            </div>
                        </div>

                        {/* Image Preview Thumbnail */}
                        <div className="w-full md:w-56 xl:w-72 aspect-video rounded-sm overflow-hidden border border-teal-accent/10 shrink-0 relative group-hover/prompt:border-teal-accent/40 transition-colors z-10">
                            <div className="absolute inset-0 bg-teal-accent/20 mix-blend-overlay opacity-0 group-hover/prompt:opacity-100 transition-opacity z-10 pointer-events-none" />
