            </AnimatePresence>
        </section>
    );
}

"use client";

import { motion, AnimatePresence } from "framer-motion";
import { useState, useEffect, useRef } from "react";
import dynamic from "next/dynamic";

const Artifact3D = dynamic(() => import("./Artifact3D"), { ssr: false });

const backgroundVideos = [
    "/assets/hero_videos/abtf_rue.mp4",
    "/assets/hero_videos/alexis_andre_cave.mp4",
    "/assets/hero_videos/atalntis_flood.mp4",
    "/assets/hero_videos/atlantis_blue_to_red.mp4",
    "/assets/hero_videos/atlantis_collapse.mp4",
    "/assets/hero_videos/atlantis_collapse2.mp4",
    "/assets/hero_videos/atlantis_inside_blue.mp4",
    "/assets/hero_videos/atlantis_inside_red_blue.mp4",
    "/assets/hero_videos/atlantis_underwater.mp4",
    "/assets/hero_videos/bea_conspi.mp4",
    "/assets/hero_videos/charles_titanic.mp4",
    "/assets/hero_videos/charles_titanic_detail_mp4.mp4",
    "/assets/hero_videos/cocomploteurs_verite_x.mp4",
    "/assets/hero_videos/creature3_optimized.webm",
    "/assets/hero_videos/tall_whites.mp4"
];

export default function Hero({ onPlayTrailer }: { onPlayTrailer: () => void }) {
    // 13 correspond à "/assets/hero_videos/creature3_optimized.webm", format ultra-léger
    const [currentVideoIndex, setCurrentVideoIndex] = useState(13);
    const [isVideoLoaded, setIsVideoLoaded] = useState(false);
    const containerRef = useRef<HTMLDivElement>(null);
    const videoRef = useRef<HTMLVideoElement>(null);

    // Ne pas changer la vidéo initiale au montage pour garantir le SSR de bea_conspi.
    // L'aléatoire ne s'applique qu'aux vidéos SUIVANTES.
    // useEffect(() => {
    //     setCurrentVideoIndex(Math.floor(Math.random() * backgroundVideos.length));
    // }, []);

    const handleVideoEnd = () => {
        setIsVideoLoaded(false); // Reset loading state for next video
        let nextIndex = Math.floor(Math.random() * backgroundVideos.length);
        if (nextIndex === currentVideoIndex) {
            nextIndex = (currentVideoIndex + 1) % backgroundVideos.length;
        }
        setCurrentVideoIndex(nextIndex);
    };

    // Use IntersectionObserver to play/pause video based on viewport visibility
    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    const video = videoRef.current;
                    if (!video) return;

                    if (entry.isIntersecting) {
                        // Restart or resume playback if it was paused by the browser
                        video.play().catch(e => console.log("Hero auto-play blocked or delayed:", e));
                    } else {
                        // Optional: intentionally pause when severely out of view to save resources
                        video.pause();
                    }
                });
            },
            { threshold: 0.1 } // Trigger when at least 10% is visible
        );

        if (containerRef.current) observer.observe(containerRef.current);

        return () => observer.disconnect();
    }, [currentVideoIndex]); // Re-bind if the video changes

    return (
        <section ref={containerRef} className="relative w-full h-screen overflow-hidden bg-black flex flex-col justify-end items-center pb-16">

            {/* Loader Overlay */}
            <AnimatePresence>
                {!isVideoLoaded && (
                    <motion.div
                        initial={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        transition={{ duration: 0.8 }}
                        className="absolute inset-0 z-[5] bg-transparent flex flex-col items-center justify-center pointer-events-none"
                    >
                        <motion.div
                            animate={{ opacity: [0.3, 1, 0.3] }}
                            transition={{ duration: 1.5, repeat: Infinity }}
                            className="flex flex-col items-center gap-4"
                        >
                            <div className="w-16 h-16 border-t-2 border-r-2 border-teal-accent/80 rounded-full animate-spin" />
                            <span className="text-[10px] font-mono text-teal-accent tracking-[0.4em] uppercase">
                                Initialisation du flux visuel...
                            </span>
                            <span className="text-[8px] font-mono text-white/30 tracking-widest">
                                BYPASSING SECURITY MEASURES
                            </span>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Background Video Cover */}
            <motion.div
                initial={{ scale: 1.1, opacity: 0 }}
                animate={{ scale: 1, opacity: 0.6 }}
                transition={{ duration: 1.5, ease: "easeOut" }}
                className="absolute inset-0 z-0 bg-black"
            >
                {/* L'attribut preload="auto" indique au navigateur de télécharger la vidéo au plus vite */}
                {/* Suppression de "isMounted" : la balise existe dès le HTML serveur */}
                <video
                    ref={videoRef}
                    key={backgroundVideos[currentVideoIndex]}
                    src={backgroundVideos[currentVideoIndex]}
                    poster="/assets/hero_videos/creature3_poster.jpg"
                    className={`w-full h-full object-cover object-center transition-opacity duration-1000 ${isVideoLoaded ? 'opacity-100' : 'opacity-0'}`}
                    autoPlay
                    muted
                    playsInline
                    preload="auto"
                    onCanPlay={() => setIsVideoLoaded(true)}
                    onEnded={handleVideoEnd}
                />
                {/* Gradient Overlay */}
                <div className="absolute inset-0 bg-gradient-to-t from-background via-background/30 to-transparent" />
            </motion.div>

            {/* Content — pushed to bottom left for cinematic asymmetry */}
            <div className="relative z-10 w-full max-w-5xl px-4 md:px-8 flex flex-col items-start">

                {/* Classified label */}
                <motion.div
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.6, delay: 0.2 }}
                    className="flex items-center gap-3 mb-3"
                >
                    <div className="w-4 h-px bg-teal-accent/60" />
                    <span className="text-[9px] font-mono text-teal-accent/70 uppercase tracking-[0.4em]">
                        Projet de film / 3D / Animation de Lowrenzu
                    </span>
                    <div className="w-1 h-1 rounded-full bg-teal-accent/50 animate-pulse" />
                </motion.div>

                {/* Main title block */}
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 1, delay: 0.4, ease: "easeOut" }}
                    className="flex flex-col items-start w-full relative group"
                >
                    {/* Decorative vertical line accent */}
                    <div className="absolute -left-6 top-0 bottom-0 w-[2px] bg-gradient-to-b from-teal-accent/60 via-teal-accent/10 to-transparent" />

                    {/* L'Artefact — Layered Glitch Style */}
                    <div className="relative">
                        <motion.h1
                            className="text-4xl md:text-8xl font-black uppercase leading-none tracking-tighter relative z-10"
                            style={{
                                color: "#ffffff",
                                textShadow: "0 0 40px rgba(37,209,244,0.3)"
                            }}
                        >
                            <span className="relative inline-block">
                                L'Artefact
                                {/* Glitch Layer 1 - Cyan Shift */}
                                <motion.span
                                    className="absolute inset-0 text-teal-accent opacity-50 mix-blend-screen pointer-events-none"
                                    animate={{
                                        x: [-2, 2, -1, 0],
                                        opacity: [0.5, 0.2, 0.5, 0]
                                    }}
                                    transition={{
                                        duration: 0.2,
                                        repeat: Infinity,
                                        repeatDelay: Math.random() * 5 + 2
                                    }}
                                >
                                    L'Artefact
                                </motion.span>
                                {/* Glitch Layer 2 - Magenta-ish/White Highlight */}
                                <motion.span
                                    className="absolute inset-0 text-white opacity-0 pointer-events-none"
                                    animate={{
                                        clipPath: [
                                            "inset(80% 0 0 0)",
                                            "inset(10% 0 70% 0)",
                                            "inset(50% 0 30% 0)",
                                            "inset(0 0 0 0)"
                                        ],
                                        opacity: [0, 0.8, 0, 0.3, 0],
                                        x: [0, -5, 5, 0]
                                    }}
                                    transition={{
                                        duration: 0.4,
                                        repeat: Infinity,
                                        repeatDelay: Math.random() * 8 + 3
                                    }}
                                >
                                    L'Artefact
                                </motion.span>
                            </span>
                        </motion.h1>

                        {/* Background glowing aura */}
                        <div className="absolute inset-0 bg-teal-accent/5 blur-[80px] -z-10 rounded-full" />
                    </div>

                    {/* Elegant shimmer separator */}
                    <div className="relative w-full max-w-2xl h-px mt-4 mb-2 overflow-hidden bg-white/10">
                        <motion.div
                            className="absolute inset-0"
                            style={{
                                background: "linear-gradient(to right, #25d1f4 0%, transparent 80%)",
                                opacity: 0.6
                            }}
                            animate={{ opacity: [0.3, 0.7, 0.3] }}
                            transition={{ duration: 2, repeat: Infinity }}
                        />
                        <motion.div
                            className="absolute top-0 h-full w-32"
                            style={{
                                background: "linear-gradient(to right, transparent, rgba(37,209,244,0.8), transparent)",
                            }}
                            animate={{ x: ["-100%", "500%"] }}
                            transition={{ duration: 4, repeat: Infinity, ease: "linear" }}
                        />
                    </div>

                    {/* de la Vérité — Staggered reveal, high technical font */}
                    <div className="w-full max-w-2xl flex justify-between items-end pl-1">
                        <div className="flex flex-col">
                            <motion.span
                                initial={{ opacity: 0, x: -10 }}
                                animate={{ opacity: 1, x: 0 }}
                                transition={{ delay: 1.2 }}
                                className="text-[10px] font-mono text-teal-accent/50 tracking-[0.6em] uppercase mb-1"
                            >
                                Operation // Truth_Seeker
                            </motion.span>
                            <div className="flex items-baseline gap-4">
                                <motion.h2
                                    className="text-lg sm:text-xl md:text-3xl font-light uppercase tracking-[0.4em] text-teal-accent/90"
                                    style={{
                                        fontFamily: "var(--font-outfit)",
                                        textShadow: "0 0 20px rgba(37,209,244,0.4)"
                                    }}
                                >
                                    de la Vérité
                                </motion.h2>
                                <motion.div
                                    className="h-[2px] flex-1 bg-gradient-to-r from-teal-accent/40 to-transparent mb-2 hidden md:block"
                                    initial={{ scaleX: 0 }}
                                    animate={{ scaleX: 1 }}
                                    transition={{ delay: 1.5, duration: 1 }}
                                />
                            </div>
                        </div>

                        {/* Technical Coordinates / Meta info */}
                        <div className="text-right hidden sm:block">
                            <span className="block text-[7px] font-mono text-white/20 tracking-tighter">DATASET_ID: ATF_01-V</span>
                            <span className="block text-[7px] font-mono text-white/20 tracking-tighter">COORD: 45.4215° N, 75.6972° W</span>
                        </div>
                    </div>
                </motion.div>

                {/* Scroll hint */}
                <motion.div
                    initial={{ opacity: 0 }}
